--메뉴 테이블
create table menu(
 mid varchar(50) primary key not null,
 category varchar(50) not null,
 name varchar(50) not null,
 price15 int(10),
 price30 int(10),
 event varchar(2),
 cal int(5),
 img varchar(200)
);

--임시 테이블 (주문테이블에 입력 전 임시주문)
create table temporders(
 mid varchar(50) not null,
 mname varchar(50) not null,
 price int(10) not null,
 options varchar(1000),
 qnt int(100) not null,
 optionprice int(10) not null
);


--주문 테이블 (*주의: order's'! order로 하면 생성오류남)
create table orders(
 ordernum varchar(20) not null,
 mid varchar(50) not null,
 canclenum varchar(20),
 status varchar(10),
 options varchar(1000),
 qnt int(100) not null,
 totalprice int(10) not null,
 regdate date not null,
 togo varchar(10),
 constraint fk_mid foreign key (mid) references menu (mid) on delete cascade
);

--관리자 테이블 (1명만 등록 가능-> count>0 이면 등록 불가)
create table manager(
 id varchar(50) not null,
 pw varchar(50) not null,
 name varchar(20),
 hpnum varchar(20),
 address varchar(20) 
);


--매출 테이블
create table sales(
 regdate datetime not null, 
 opentime varchar(50),
 closetime varchar(50),
 salcount int(20), 
 totalsal int(20) not null  
);

